import org.junit.Assert;
import org.junit.Test;


public class SherlockStringTest { 

    @Test
    public void testIsValid() throws Exception {
        Assert.assertEquals("YES", SherlockString.isValid("ahoj"));
        Assert.assertEquals("YES", SherlockString.isValid("ahossj"));
        Assert.assertEquals("NO", SherlockString.isValid("ahosddsj"));
        Assert.assertEquals("NO", SherlockString.isValid("ahosssj"));
        Assert.assertEquals("NO", SherlockString.isValid("aaaaaahhhhhosssj"));
        Assert.assertEquals("YES", SherlockString.isValid("aabbc"));
        Assert.assertEquals("YES", SherlockString.isValid(""));
    }


} 
